select p.id,from_unixtime(p.skrad_dags), p.starfsmadur_id, n.nafn, sum(m.fjoldi) as msum, sum(v.fjoldi) as vsum, sum(d.fjoldi) as dsum from pontun as p left join sk_mat as m on p.id = m.pontun_id left join sk_val as v on v.pontun_id = p.id left join sk_drykk as d on p.id = d.pontun_id left join notandi as n on p.starfsmadur_id = n.id where matsedill_id > 300 and stada = 1 group by p.id having msum is null and vsum is null and dsum is null;
